mapper.select:
SELECT *
FROM TOPIC
WHERE TOPIC_WRITER = #{topicWriter}
ORDER BY TOPIC_ID DESC
LIMIT #{criteria.pageStart}, #{criteria.amount}

SELECT COUNT(*) AS total
FROM TOPIC
WHERE TOPIC_WRITER = #{topicWriter}

mapper.update:
UPDATE TOPIC
SET TOPIC_NAME = #{topicName},
    TOPIC_CONTENT = #{topicContent}
WHERE TOPIC_ID = #{topicId}
